mapper.select:
SELECT c.*
  FROM customer c
 WHERE userid = #{userid}

mapper.update:
UPDATE customer c
   SET c.name = #{name}
     , c.birthyear = #{birthyear}
     , c.address = #{address}
     , c.phone = #{phone}
     , c.grade = #{grade}
     , c.moddate = sysdate
 WHERE c.userid = #{userid}
